from django.db import models# Create your models here.class signupdb(models.Model):    User_Name=models.CharField(max_length=30,null=True,blank=True)    Email=models.EmailField(max_length=40,null=True,blank=True)    Phone_number=models.IntegerField(null=True,blank=True)    Password=models.CharField(max_length=60,null=True,blank=True)    User_Image=models.ImageField(upload_to="user_dp",null=True,blank=True)class contactdb(models.Model):    Name=models.CharField(max_length=30,null=True,blank=True)    Number=models.IntegerField(null=True,blank=True)    Email=models.EmailField(max_length=40,null=True,blank=True)    Subject=models.CharField(max_length=40,null=True,blank=True)    Message=models.CharField(max_length=40,null=True,blank=True)class cartdb(models.Model):    User_name=models.CharField(max_length=50,null=True,blank=True)    Product_name=models.CharField(max_length=30,null=True,blank=True)    Price=models.IntegerField(null=True,blank=True)    Description=models.CharField(max_length=59,null=True,blank=True)    Quantity=models.IntegerField(null=True,blank=True)class checkoutdb(models.Model):    Fist_Name = models.CharField(max_length=50, null=True, blank=True)    Last_Name = models.CharField(max_length=50, null=True, blank=True)    Email = models.EmailField(max_length=50, null=True, blank=True)    Mobile = models.IntegerField(null=True, blank=True)    AdressoOne = models.CharField(max_length=50, null=True, blank=True)    AddressTwo = models.CharField(max_length=50, null=True, blank=True)    Country = models.CharField(max_length=50, null=True, blank=True)    City = models.CharField(max_length=50, null=True, blank=True)    State = models.CharField(max_length=50, null=True, blank=True)    Zipcode= models.IntegerField(null=True, blank=True)